{
  "html": "<div id='textToSpeechContainer'>
<input id='inputTextField' type='text' placeholder='Enter text here...'>
<button id='speakBtn'>Speak</button>
</div>
<script>
window.SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
const synth = window.speechSynthesis;
const speakBtn = document.getElementById('speakBtn');
const inputTextField = document.getElementById('inputTextField');
speakBtn.addEventListener('click', ()=>{
var utterance = new SpeechSynthesisUtterance(inputTextField.value);
synth.speak(utterance);
});
</script>"
}
