document.getElementById('url_form').addEventListener('submit', function(e) {
  e.preventDefault();
  var url = document.getElementById('url_input').value;
  try {
    new URL(url);
    document.getElementById('validity').innerText = 'Valid URL.';
  } catch (_) {
    document.getElementById('validity').innerText = 'Invalid URL.';
  }
});
